{"defaultCompiler":{"version":"0.8.15"},"sources":{"contracts/FarmlyUniV3Executor.sol":{"content":"pragma solidity >=0.5.0;
pragma abicoder v2;

import "./library/LiquidityAmountsLib.sol";
import "./library/FarmlyFullMath.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Factory.sol";
import "@uniswap/v3-core/contracts/libraries/TickMath.sol";
import "@uniswap/v3-core/contracts/libraries/FullMath.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "@uniswap/v3-periphery/contracts/interfaces/INonfungiblePositionManager.sol";
import "@uniswap/v3-periphery/contracts/interfaces/IQuoter.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "@uniswap/v3-periphery/contracts/libraries/TransferHelper.sol";
import "@uniswap/v3-periphery/contracts/base/LiquidityManagement.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

contract FarmlyUniV3Executor is IERC721Receiver, LiquidityAmountsLib {
    INonfungiblePositionManager public constant nonfungiblePositionManager =
        INonfungiblePositionManager(0xC36442b4a4522E871399CD717aBDD847Ab11FE88);

    ISwapRouter public constant swapRouter =
        ISwapRouter(0xE592427A0AEce92De3Edee1F18E0157C05861564);

    IUniswapV3Factory public constant factory =
        IUniswapV3Factory(0x1F98431c8aD98523631AE4a59f267346ea31F984);
    IQuoter public constant quoter =
        IQuoter(0xb27308f9F90D607463bb33eA1BeBb41C27CE5AB6);

    function onERC721Received(
        address operator,
        address,
        uint256 tokenId,
        bytes calldata
    ) external override returns (bytes4) {
        nonfungiblePositionManager.transferFrom(
            address(this),
            tx.origin,
            tokenId
        );
        return this.onERC721Received.selector;
    }

    function execute(
        uint160 sqrtRatioAX96,
        uint160 sqrtRatioBX96,
        uint256 amount0Has,
        uint256 amount1Has,
        IERC20Metadata token0,
        IERC20Metadata token1,
        uint24 poolFee
    ) public {
        token0.transferFrom(msg.sender, address(this), amount0Has);
        token1.transferFrom(msg.sender, address(this), amount1Has);
        IUniswapV3Pool pool = IUniswapV3Pool(
            factory.getPool(address(token0), address(token1), poolFee)
        );
        (uint160 sqrtPriceX96, , , , , , ) = pool.slot0();

        (uint amount0, uint amount1, bool is01) = LiquidityAmountsLib
            .getAmountsForAddingLiquidity(
                FarmlyFullMath.mulDiv(
                    (sqrtPriceX96 ** 2),
                    10 ** token0.decimals(),
                    2 ** 192
                ),
                sqrtPriceX96,
                sqrtRatioAX96,
                sqrtRatioBX96,
                amount0Has,
                amount1Has,
                token0.decimals(),
                token1.decimals()
            );

        if (is01) {
            swap(
                address(token0),
                address(token1),
                amount0Has - amount0,
                poolFee
            );
        } else {
            swap(
                address(token1),
                address(token0),
                amount1Has - amount1,
                poolFee
            );
        }

        add(
            address(token0),
            address(token1),
            token0.balanceOf(address(this)),
            token1.balanceOf(address(this)),
            poolFee
        );
    }

    function swap(
        address tokenIn,
        address tokenOut,
        uint amountIn,
        uint24 poolFee
    ) private returns (uint amountOut) {
        TransferHelper.safeApprove(tokenIn, address(swapRouter), amountIn);

        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter
            .ExactInputSingleParams({
                tokenIn: tokenIn,
                tokenOut: tokenOut,
                fee: poolFee,
                recipient: msg.sender,
                deadline: block.timestamp,
                amountIn: amountIn,
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            });

        // The call to `exactInputSingle` executes the swap.
        amountOut = swapRouter.exactInputSingle(params);
    }

    function add(
        address token0,
        address token1,
        uint amount0ToMint,
        uint amount1ToMint,
        uint24 poolFee
    )
        private
        returns (
            uint256 tokenId,
            uint128 liquidity,
            uint256 amount0,
            uint256 amount1
        )
    {
        TransferHelper.safeApprove(
            token0,
            address(nonfungiblePositionManager),
            amount0ToMint
        );
        TransferHelper.safeApprove(
            token1,
            address(nonfungiblePositionManager),
            amount1ToMint
        );

        INonfungiblePositionManager.MintParams
            memory params = INonfungiblePositionManager.MintParams({
                token0: token0,
                token1: token1,
                fee: poolFee,
                tickLower: TickMath.MIN_TICK,
                tickUpper: TickMath.MAX_TICK,
                amount0Desired: amount0ToMint,
                amount1Desired: amount1ToMint,
                amount0Min: 0,
                amount1Min: 0,
                recipient: address(this),
                deadline: block.timestamp
            });

        // Note that the pool defined by DAI/USDC and fee tier 0.3% must already be created and initialized in order to mint
        (tokenId, liquidity, amount0, amount1) = nonfungiblePositionManager
            .mint(params);
    }
}
